import React, { useState } from "react";
import "./Modal.css";
const Modal = (props) => {
    let [item, setItem] = useState(props.editTodo);
    let newobj = { ...item };
    function editName(e) {
        newobj.name = e.target.value;
        setItem(newobj);
    }
    function editSurname(e) {
        newobj.surname = e.target.value;
        setItem(newobj);
    }
    function editNumber(e) {
        newobj.number = e.target.value;
        setItem(newobj);
    }
   
    function handleSave() {
        props.handleSaveCont(item);
    }
    return (
        <div className="main-edit-block">
            <div className="edit-block">
                <h1>edit</h1>
                <div className="input-block">
                    <input
                        onChange={editName}
                        value={item.name}
                        type="text"
                        className="input"
                        placeholder="name"
                    />
                    <input
                        onChange={editSurname}
                        value={item.surname}
                        type="text"
                        className="input"
                        placeholder="surname"
                    />
                    <input
                        onChange={editNumber}
                        value={item.number}
                        type="number"
                        className="input"
                        placeholder="number"
                    />
                    
                </div>
                <div className="btn-block">
                    <button onClick={handleSave}>save</button>
                    <button onClick={() => props.handleClose()}>close</button>
                    <button onClick={()=> props.handleClose()}></button>
                </div>
            </div>
        </div>
    );
};

export default Modal;
